mapper.insert:
insert into comment (aid, cid, uid, comment_of_cid, comment_content, comment_date
)

values (#{aid}, #{cid}, #{uid},
#{commentOfCid}, #{commentContent}, #{commentDate})


select #{aid}, IFNULL(Max(cid),0)+1, #{uid}, #{commentOfCid}, #{commentContent}, #{commentDate}
    from comment
    where aid = #{aid}
