mapper.select:
select 

from marked
where uid = #{uid,jdbcType=INTEGER}
  and mark_id = #{markId,jdbcType=INTEGER}

mapper.delete:
delete from marked
where uid = #{uid,jdbcType=INTEGER}
  and mark_id = #{markId,jdbcType=INTEGER}

mapper.insert:
insert into marked (uid, mark_id, mark_name
  )
values (#{uid,jdbcType=INTEGER}, #{markId,jdbcType=INTEGER}, #{markName,jdbcType=VARCHAR}
  )

mapper.update:
update marked
set mark_name = #{markName,jdbcType=VARCHAR}
where uid = #{uid,jdbcType=INTEGER}
  and mark_id = #{markId,jdbcType=INTEGER}
